import React from 'react'
import './Auth.css'
import {
    BrowserRouter as Router, Route, Redirect, withRouter
} from 'react-router-dom'
import Feed from "../feed/Feed"

const Authentication = {
    isAuthenticated: false,
    setAuthentication(cb) {
        this.isAuthenticated = true

        setTimeout(cb, 100)
    },
    signout(cb) {
        this.isAuthenticated = false
        setTimeout(cb, 100)

    }
}

function loginRequest(user, password) {
    console.log(user)
    var bodyData = JSON.stringify({
        'login': user,
        'password': password
    })
    // console.log(bodyData)
    var request = new Request('http://localhost:8080/login', {
        body: bodyData,
        method: 'post',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        }
    });
    fetch(request).then(function (data) {
        console.log("data", data)
        return data.blob();
    })
}

class Login extends React.Component {
    state = {
        redirect: false,
        user: "",
        password: "",
    }
    login = () => {

        let user = loginRequest(this.state.user, this.state.password)
        console.log("user", user);
        if (this.state.user === "") {
            Authentication.setAuthentication(() => {
                this.setState(() => ({
                    redirect: true
                }))
            })
        }
    }
    render() {
        if (this.state.redirect === true) {
            return <Redirect to='/feed' />
        }


        return (
            < div className="Login">
                <div >
                    <input type="text"
                        value={this.state.value}
                        onChange={event => this.setState({ user: event.target.value })}></input>
                </div>

                <div>
                    <input type="text"
                        value={this.state.value}
                        onChange={event => this.setState({ password: event.target.value })}></input>
                </div>
                <button onClick={this.login}>Log in</button>
            </div >
        )
    }
}



const LoginSucess = withRouter(() => (
    // Se isAuthenticated === True renderiza Feed se False renderiza texto.
    Authentication.isAuthenticated ? (
        < Feed />
    ) : (
            <div className="Login">

                <p>Por Favor Logue No QQ_Twitter</p>
            </div>
        )
))

export default function AuthExample() {
    return (
        <div>
            <Router>
                <LoginSucess >
                    {/* Renderizando p√°gina depois do login com sucesso*/}
                    <link to='/feed'></link>
                </LoginSucess>
                <Route path='/' component={Login} />
            </Router>
        </div>
    )
}
