import pg from 'pg'
const { Client } = require('pg');

const client = new Client({
    user: "postgres",
    password: "entersandman",
    host: 'localhost',
    port: 5432,
    database: 'qq_twitter'
});


export function QueryLoginData(login) {
    client.connect()
        .then((console.log("Connected")))
        .then(() => client.query("select * from user_data where login = $1", [login]))
        .then(result => console.table(result.rows))
        .catch(e => console.log(e))
        .finally(() => client.end())
}

